import React from 'react';
import './App.css';

class App extends React.Component {

  constructor() {
    super()
    this.state = {
      numeroDeCliques: 0,
    }
  }

  btnFunc = () => {
    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques: estadoAnterior.numeroDeCliques + 1,
    }))
  }

  render() {
    return <button onClick={this.btnFunc}>texto do click {this.numeroDeCliques}</button>
  }
}

export default App;
