import React from 'react';
import './App.css';

class App extends React.Component {

  constructor() {
    super()
    this.btnFunc = this.btnFunc.bind(this)
  }

  btnFunc = () => {
    this.setState({
      numeroDeCliques: 1,
    })
  }

  render() {
    return <button onClick={this.btnFunc}>texto do click</button>
  }
}

export default App;
